import Foundation

class GMDCaseGenerator {
    static func printNewCases() {
        print("\n----Google Material Design\n\n")
        
        for iconName in iconNames {
            if let foundCase = caseLookup[iconName] {
                let hex = String(format:"%02X", foundCase.rawValue)

                print("case \(iconName) = 0x\(hex)")
            }
            else {
                print("//case \(iconName) = 0xEA")
            }
        }
    }

    static var caseLookup:[String:GMDIcon] = {
        var lookup:[String:GMDIcon] = [:]
        for item in GMDIcon.allCases {
            lookup["\(item)"] = item
        }
        return lookup
    }()

